// ---------------------------------------------------------------
// Process: SC_METHOD wait_for_end
// Static sensitivity: sensitive << clk.pos() << testbench_end_event;

void testbench::wait_for_end() {
   // If run() has not finished, we do nothing here
   if (!testbench_ended) return;
   // check for completed outputs
   if (result_comp->get_compare_count() < result_capture_count) {testbench_end_event.notify(1,SC_NS); return;}
   // If we made it here, all outputs have flushed. Check the results
   SC_REPORT_INFO(name(), "Simulation completed");
   check_results();
   sc_stop();
}
// ---------------------------------------------------------------
// Process: SC_THREAD run
// Static sensitivity: 

void testbench::run() {
   enable_idle_sync_mode = false;
   idle_sync_stable_cycles = 1;
   input_ignore = false;
   input_skip = false;
   input_skip_quiet = false;
   input_skip_once = false;
   input_skip_noerr = false;
   input_array_comp_first = -1;
   input_array_comp_last = -1;
   input_wait_ctrl.clear();
   input_capture_count = 0;
   input_iteration_count = 0;
   result_ignore = false;
   result_skip = false;
   result_skip_quiet = false;
   result_skip_once = false;
   result_skip_noerr = false;
   result_array_comp_first = -1;
   result_array_comp_last = -1;
   result_use_mask = false;
   result_output_mask = ~0;
   result_wait_ctrl.clear();
   result_capture_count = 0;
   result_iteration_count = 0;
   main_exit_code = main();
   cout<<"Info: Execution of user-supplied C++ testbench 'main()' has completed with exit code = " << main_exit_code << endl;
   cout<<endl;
   cout<<"Info: Collecting data completed"<<endl;
   cout<<"   captured "<<input_capture_count<<" values of input"<<endl;
   cout<<"   captured "<<result_capture_count<<" values of result"<<endl;
   testbench_ended = true;
   testbench_end_event.notify(SC_ZERO_TIME);
}
